import{_ as e,c as o,o as t,d as r}from"./app.76054421.js";const g=JSON.parse('{"title":"Importing and exporting notes","description":"","frontmatter":{},"headers":[{"level":2,"title":"Importing from Evernote","slug":"importing-from-evernote","link":"#importing-from-evernote","children":[]}],"relativePath":"p/2ba8366ac77c4a93b9eb7595d1343eb6.md"}'),a={name:"p/2ba8366ac77c4a93b9eb7595d1343eb6.md"},n=r('<h1 id="importing-and-exporting-notes" tabindex="-1">Importing and exporting notes <a class="header-anchor" href="#importing-and-exporting-notes" aria-hidden="true">#</a></h1><h2 id="importing-from-evernote" tabindex="-1">Importing from Evernote <a class="header-anchor" href="#importing-from-evernote" aria-hidden="true">#</a></h2><p>Joplin can import complete Evernote notebooks, as well as notes, tags, images, attached files and note metadata (such as author, geo-location, etc.) via ENEX files.</p><p>To import Evernote data, first export your Evernote notebooks to ENEX files as described <a href="https://help.evernote.com/hc/en-us/articles/209005557-How-to-back-up-export-and-restore-import-notes-and-notebooks" target="_blank" rel="noreferrer">here</a>. Then, on <strong>desktop</strong>, do the following: Open File &gt; Import &gt; ENEX and select your file. The notes will be imported into a new separate notebook. If needed they can then be moved to a different notebook, or the notebook can be renamed, etc. Read <a href="https://joplinapp.org/help/#importing-from-evernote" target="_blank" rel="noreferrer">more about Evernote import</a>.</p><h1 id="importing-from-other-apps" tabindex="-1">Importing from other apps <a class="header-anchor" href="#importing-from-other-apps" aria-hidden="true">#</a></h1><p>Joplin can also import notes from <a href="https://github.com/laurent22/joplin#importing-from-other-applications" target="_blank" rel="noreferrer">many other apps</a> as well as <a href="https://github.com/laurent22/joplin#importing-from-markdown-files" target="_blank" rel="noreferrer">from Markdown or text files</a>.</p><h1 id="exporting-notes" tabindex="-1">Exporting notes <a class="header-anchor" href="#exporting-notes" aria-hidden="true">#</a></h1><p>Joplin can export to the JEX format (Joplin Export file), which is an archive that can contain multiple notes, notebooks, etc. This is a format mostly designed for backup purposes. You may also export to other formats such as plain Markdown files, to JSON or to PDF. Find out <a href="https://github.com/laurent22/joplin#exporting" target="_blank" rel="noreferrer">more about exporting notes</a> on the official website.</p>',8),i=[n];function p(s,h,l,d,m,c){return t(),o("div",null,i)}const b=e(a,[["render",p]]);export{g as __pageData,b as default};
